function hostname = host( hostname )%HOST returns lowercase hostname from environment variable%% HOSTNAME = HOST()%   returns current host%    % HOSTNAME = HOST( HOSTNAME )%   sets hostname to HOSTNAME%% HOSTNAME = HOST('')%   resets hostname to system host%% Environment variables can be set by%    Windows XP: with Start|Control Panel|System|Advanced%    Linux, bash: with "export HOST='eto'" in .bashrc%    Linux, tcsh: with "setenv HOST eto" in .tcshrc%% An exception is made for the Apple MAC which is returned as 'mac'%% 2008-2019, Alexander Heimel%persistent hostname_persif nargin>0    hostname_pers = hostname;endif isempty(hostname_pers)    if isunix        [dummy,hostname_pers] = system('hostname');        hostname_pers = strtrim(hostname_pers);    end    if isempty(hostname_pers)        hostname_pers=lower(getenv('host'));    end    if isempty(hostname_pers)        hostname_pers=lower(getenv('HOST'));    end    if isempty(hostname_pers) && usejava('jvm') && ~exist('OCTAVE_VERSION', 'builtin')        hostname_pers = lower(char( getHostName( java.net.InetAddress.getLocalHost ) ));    end    if isempty(hostname_pers)         hostname_pers = lower(getenv('COMPUTERNAME'));    end    if isempty(hostname_pers) && strcmp(computer,'MAC2')  %#ok<AND2> % only one MAC available        hostname_pers = 'mac';    endendhostname = hostname_pers;mlock