function NelsonLabToolsInitLocal% inits local pathname variables for NelsonLabTools, now only used for% ephys runexperiment start folderglobal  initextractpathNLTswitch computer    case {'PCWIN','PCWIN64'}        datadir = get_datadir;    case {'LNX86','GLNX86','GLNXA64'}        datadir='/home/data/';    case {'MACI64'}        datadir='/tmp/';    otherwise        logmsg(['Do not know what to do for computer type ' computer ]);        datadir='';endinitextractpathNLT = fullfile(datadir,filesep);function datadir = get_datadirdatadir = fullfile('C:','Data','InVivo','Electrophys',capitalize(host));if exist(datadir,'dir')    returnenddatadir = fullfile('D:','Data','InVivo','Electrophys',capitalize(host));if exist(datadir,'dir')    returnenddatadir = fullfile('C:','Data');if exist(datadir,'dir')    returnenddatadir = fullfile('D:','Data');if exist(datadir,'dir')    returnelse    logmsg('No Data folder found.');end